double rand_polar()
{
    static double stored;
    static bool hasStored = false;

    if(hasStored)
    {
        hasStored = false;
        return stored;
    }

    double v1, v2, w;

    do
    {
        v1 = (double)rand() / RAND_MAX * 2.0 - 1;
        v2 = (double)rand() / RAND_MAX * 2.0 - 1;
        w = v1 * v1 + v2 * v2;
    } while(w > 1);

    double r = sqrt(-2.0 * log(w) / w);

    hasStored = true;
    stored = r * v2;

    return r * v1;
}

